import React, { useRef } from "react";
import "app.css"

function App() {
  const parentRef = useRef(null);

  const handleScroll = (event) => {
    const scrollPosition = window.scrollY;
    const pageHeight = window.innerHeight;

    if (event.deltaY > 0) {
      // Scrolling down
      const nextPageTop = Math.ceil(window.scrollY / pageHeight) * pageHeight;
      window.scrollTo({ top: nextPageTop, behavior: "smooth" });
    } else if (event.deltaY < 0) {
      // Scrolling up
      const prevPageTop = Math.floor(window.scrollY / pageHeight) * pageHeight;
      window.scrollTo({ top: prevPageTop, behavior: "smooth" });
    }
  };


  return (
    <div onWheel={handleScroll}>
      <div className="container" id="introContainer">
        <div id="intro">
          <h1>Luka Koens</h1>
          <h2> curriculum vitae</h2>
          <ul>
            <li>Email: luka.koens@gmail.com</li>
            <li>Mobile: 0279072766</li>
          </ul>        
        </div>
        <iframe
          title="Geospatial Introduction"
          id="map"
          src="https://ion.cesium.com/stories/viewer/?id=d3ada2d2-9849-485b-a6e8-7d849bdd3d71"
          allowFullScreen={false}
          mozallowfullscreen="false"
          webkitallowfullscreen="false"
        ></iframe>
      </div>
      <div className="divider"></div>
      <div className="container" id="gaussianContainer">
        <div id="gaussianText">
          <h1 id="gaussianHeading">3D Gaussians</h1>
          <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
